declare const run: unique symbol;
declare type Deps = undefined | string[];
declare type ModelData = {
    [key: string]: any;
};
declare type LitModel = ModelData & {
    [run]: {
        deps: Deps;
        setState: (newState: ModelData) => void;
    }[];
};
declare type Now = (next?: undefined | Model | ModelData) => any;
declare type Model = (now: Now) => ModelData;
declare type UseModel = (model: Model, deps?: Deps) => LitModel;
declare const useModel: UseModel;

export default useModel;
export { Model };
